#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="D:\doc\cli\vue-template\node_modules\.pnpm\esbuild@0.13.12\node_modules\esbuild\bin\node_modules;D:\doc\cli\vue-template\node_modules\.pnpm\esbuild@0.13.12\node_modules\esbuild\node_modules;D:\doc\cli\vue-template\node_modules\.pnpm\esbuild@0.13.12\node_modules;D:\doc\cli\vue-template\node_modules\.pnpm\node_modules;D:\doc\cli\vue-template\node_modules;D:\doc\cli\node_modules;D:\doc\node_modules;D:\node_modules;D:\doc\cli\vue-template\node_modules\.pnpm\vite@2.6.13\node_modules\vite\node_modules;D:\doc\cli\vue-template\node_modules\.pnpm\vite@2.6.13\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/doc/cli/vue-template/node_modules/.pnpm/esbuild@0.13.12/node_modules/esbuild/bin/node_modules:/mnt/d/doc/cli/vue-template/node_modules/.pnpm/esbuild@0.13.12/node_modules/esbuild/node_modules:/mnt/d/doc/cli/vue-template/node_modules/.pnpm/esbuild@0.13.12/node_modules:/mnt/d/doc/cli/vue-template/node_modules/.pnpm/node_modules:/mnt/d/doc/cli/vue-template/node_modules:/mnt/d/doc/cli/node_modules:/mnt/d/doc/node_modules:/mnt/d/node_modules:/mnt/d/doc/cli/vue-template/node_modules/.pnpm/vite@2.6.13/node_modules/vite/node_modules:/mnt/d/doc/cli/vue-template/node_modules/.pnpm/vite@2.6.13/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$env_node_path$pathsep$new_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../../../../../esbuild@0.13.12/node_modules/esbuild/bin/esbuild" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../../../../../esbuild@0.13.12/node_modules/esbuild/bin/esbuild" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../../../../../esbuild@0.13.12/node_modules/esbuild/bin/esbuild" $args
  } else {
    & "node$exe"  "$basedir/../../../../../esbuild@0.13.12/node_modules/esbuild/bin/esbuild" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
